mapper.insert:
insert into tb_diary(
	user_seq,diary_seq,title , content
) values (
	#{userSeq}, tb_diary_seq.nextval , #{title}, #{content}
)

mapper.update:
update tb_diary
set	title = #{title},
	content = #{content}
where	user_seq = #{userSeq}
and 	to_char( diary_date, 'yyyymmdd' ) = to_char( #{diaryDate}, 'yyyymmdd')
